INSERT INTO `wpmybiz_ms_snippets` VALUES (1,'Example HTML shortcode','This is an example snippet for demonstrating how to add an HTML shortcode. You can remove it, or edit it to add your own content.','\nadd_shortcode( \'shortcode_name\', function () {\n\n	$out = \'<p>write your HTML shortcode content here</p>\';\n\n	return $out;\n} );','shortcode','global',10,0,'0000-00-00 00:00:00'),(2,'Example CSS snippet','This is an example snippet for demonstrating how to add custom CSS code to your website. You can remove it, or edit it to add your own content.','\nadd_action( \'wp_head\', function () { ?>\n	<style>\n\n		/* write your CSS code here */\n\n	</style>\n<?php } );\n','css','front-end',10,0,'0000-00-00 00:00:00'),(3,'Example JavaScript snippet','This is an example snippet for demonstrating how to add custom JavaScript code to your website. You can remove it, or edit it to add your own content.','\nadd_action( \'wp_head\', function () { ?>\n	<script>\n\n		/* write your JavaScript code here */\n\n	</script>\n<?php } );\n','javascript','front-end',10,0,'0000-00-00 00:00:00'),(4,'Order snippets by name','Order snippets by name by default in the snippets table. You can remove it, or edit it to add your own content.','\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'name\';\n} );\n','code-snippets-plugin','admin',10,0,'0000-00-00 00:00:00'),(5,'Order snippets by date','Order snippets by last modification date by default in the snippets table.','\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'modified\';\n} );\n\nadd_filter( \'code_snippets/list_table/default_order\', function () {\n	return \'desc\';\n} );\n','code-snippets-plugin','admin',10,0,'2020-01-31 06:59:59');
/*!40000 ALTER TABLE `wpmybiz_ms_snippets` ENABLE KEYS */;
