INSERT INTO `wpmybiz_wc_admin_notes` VALUES (1,'wc-admin-add-first-product','info','en_US','Add your first product','Grow your revenue by adding products to your store. Add products manually, import from a sheet, or migrate from another platform.','product','{}','unactioned','woocommerce-admin','2019-12-24 13:24:30',NULL,0),(2,'wc-admin-welcome-note','info','en_US','New feature(s)','Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!','info','{}','unactioned','woocommerce-admin','2019-12-24 13:24:32',NULL,0),(3,'wc-admin-wc-helper-connection','info','en_US','Connect to WooCommerce.com','Connect to get important product notifications and updates.','info','{}','unactioned','woocommerce-admin','2019-12-24 13:24:36',NULL,0),(4,'wc-admin-mobile-app','info','en_US','Install Woo mobile app','Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.','phone','{}','unactioned','woocommerce-admin','2019-12-26 15:09:07',NULL,0),(5,'wc-admin-store-notice-giving-feedback','info','en_US','Review your experience','If you like WooCommerce Admin please leave us a 5 star rating. A huge thanks in advance!','info','{}','unactioned','woocommerce-admin','2019-12-28 07:04:23',NULL,0),(6,'wc-admin-usage-tracking-opt-in','info','en_US','Help WooCommerce improve with usage tracking','Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://mybizness.local/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.','info','{}','unactioned','woocommerce-admin','2020-01-21 11:16:00',NULL,0);
/*!40000 ALTER TABLE `wpmybiz_wc_admin_notes` ENABLE KEYS */;
UNLOCK TABLES;

